package com.mufcryan.composite_webview

import android.annotation.SuppressLint
import android.os.Bundle
import android.support.v7.app.AppCompatActivity
import kotlinx.android.synthetic.main.activity_main.*
import org.sufficientlysecure.htmltextview.HtmlHttpImageGetter


class MainActivity : AppCompatActivity() {

    @SuppressLint("JavascriptInterface")
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)
        htvContent.setHtml(html, HtmlHttpImageGetter(htvContent))
    }


    companion object {
        val url = "http://h5.sns.ivydad.com.cn/detail/297?name=%E6%96%87%E7%AB%A0%E8%AF%A6%E6%83%85"
        val html = "<!DOCTYPE html>\n" +
                "<html>\n" +
                "<meta name=\"name\" content=\"content\">\n" +
                "<meta charset=\"utf-8\">\n" +
                "<head>\n" +
                "\t<title></title>\n" +
                "</head>\n" +
                "<body>\n" +
                "<p class=\"p1\"><span class=\"s1\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\"><span label=\"大标题\" style=\"color: rgb(51, 51, 51); font-size: 22px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">家有活泼好动的娃，别忘常备这些药！- 儿科和外科医生推荐的外伤小药箱及使用方法，收藏以备不时之需</span></span></p><p class=\"p1\" style=\"text-align: center;\"><span class=\"s1\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\"><span label=\"大标题\" style=\"color: rgb(51, 51, 51); font-size: 22px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\"><img src=\"http://exploiter.oss-cn-beijing.aliyuncs.com/images/1498542926424.jpg\" alt=\"shutterstock_147286289.jpg\"/></span></span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">春夏时节，正是出门遛娃最好的时节。孩子外出难免磕磕碰碰，受了外伤如何处理？</span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">常爸特意咨询了儿科医生，整理出一个外用药药箱推荐给大家，以备不时之需。<br/></span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">本文发表前经北京同仁医院的儿科医生洪亮大夫审校，她在审校过程中特地还咨询了外科的同事，增加了很多外伤药使用方法的说明，在此对两位大夫表示感谢。</span></p><p class=\"p1\"><span label=\"标题\" style=\"color: rgb(132, 167, 64); font-size: 18px; line-height: 1; padding: 0px 0px 10px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(132, 167, 64); display: block; text-align: center; font-family: 黑体;\"><strong>先看药箱里面都是啥</strong></span><span class=\"s1\"></span></p><p class=\"p1\"><span class=\"s1\"><strong>药品类</strong></span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">碘伏（不是碘酒）</span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">双氧水</span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">百多邦（莫匹罗星软膏）</span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">无菌生理盐水</span><span class=\"s2\"></span></p><p class=\"p1\"><span class=\"s1\"><strong>工具类</strong></span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">碘伏棉签（便携好用，非常推荐！）</span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">普通棉签</span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">医用纱布</span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">创可贴</span></p><p class=\"p1\"><span label=\"正文\" style=\"color: rgb(132, 167, 64); font-size: 18px; line-height: 1; padding: 0px 0px 10px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(132, 167, 64); display: block; text-align: center; font-family: 黑体;\">药品类</span></p><p class=\"p1\"><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\"></span></p><p class=\"p1\"><span class=\"s1\"><strong>碘伏（注意，不是碘酒！）</strong></span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">用于伤口周围的消毒，不能用在破损的皮肤及眼、口粘膜上。</span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">用途：消毒。</span></p><p><span class=\"s2\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\"><img src=\"http://exploiter.oss-cn-beijing.aliyuncs.com/images/1496832051231.jpg\" alt=\"碘伏.jpg\"/></span></p><p><span style=\"font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体; color: rgb(192, 80, 77);\">为什么这里要注明“不是碘酒”呢？<br/></span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">碘伏和碘酒是两种完全不同的消毒液体：<br/></span></p><p class=\"p2\"><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">碘酒是含碘2%的酒精溶液，刺激性较大，所以不能用在破损的皮肤以及眼、口腔的粘膜上。即使是用于皮肤消毒，使用之后也需要再用75%的高浓度酒精脱碘，否则会对皮肤有一定灼伤。<br/></span></p><p class=\"p2\"><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">歪个楼：大家还有没有印象？我们小时候打针大多数情况是用碘酒消毒的。打针前，医生一般会先拿两根褐色棉签在胳膊上画两圈，再换两根白色棉签在胳膊上再画两圈，这第二次画圈就是在酒精脱碘呢。<br/></span></p><p class=\"p2\"><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">碘伏一般含碘0.5%，而且不含酒精，刺激性较小，所以更适合日常使用。<br/></span></p><p class=\"p2\"><span style=\"color: rgb(68, 68, 68); font-family: 黑体; line-…yle=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">2015年9月，辽宁营口一名13岁男孩，在学校操场被木料上的钉子扎伤了脚。家长帮他挤出血后并未再做进一步处理。几天后，男孩出现腰疼、腿发硬等情况，被诊断为破伤风，一度呼吸暂停，送入重症监护室后一直未脱离生命危险。</span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">什么时候应该及时就医，避免上述新闻中这种不幸的情况发生呢。</span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">伤口很深，或者伤口长于1厘米，需要让医生决定是否需要缝合，必要时需接种破伤风疫苗或者破伤风免疫球蛋白（另外，被生锈的钉子或者铁器割伤，也建议及时就医检查）。<br/></span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">头部外伤。因为头部外伤往往与头发粘连在一起，处理起来比较复杂，最好由专业的医生进行清理和包扎。<br/></span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">面部的切割伤、撕裂伤：面部伤口容易留下疤痕，即使伤口很小，只要损伤了真皮层，就可能造成瘢痕。这种情况建议8-12小时内到医院就诊，必要时进行缝合，避免留下终身遗憾。<br/></span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">注意：口腔周围的伤口一般需要到专门的口腔科，或者整形科去缝合，眼睛周围的伤口一般需要到专门的眼科去缝合，普通的外科是无法处理。出现这种特殊部位的伤口，建议先咨询好，再前往专门的医院，避免辗转耽误治疗时间。<br/></span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">动物咬伤、抓伤，毒虫蛰伤，无论伤口多小，都应该去看医生。<br/></span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">当伤口污染严重，无法清理干净，或自行处理后发现伤口出现红、肿、流脓等感染迹象，必须前往医院寻求帮助，避免感染进一步加重。</span><span style=\"font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体; color: rgb(0, 0, 0);\"></span><br/><span class=\"s1\"></span></p><p class=\"p2\"><span label=\"小标题\" style=\"color: rgb(51, 51, 51); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-weight: bold; font-family: 黑体;\"><strong>为什么常备药中没有红药水和紫药水？</strong></span></p><p class=\"p2\"><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\"></span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">红药水和紫药水是常爸这代人小时候最常用的外伤药，但现在已不建议使用。</span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">红药水，又叫汞溴红溶液，消毒抑菌作用较弱，遇有机物和碱性环境均会降低其作用，消毒效果并不可靠。<br/></span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">而且红药水是含有重金属汞的有机化合物，对人体有毒，对汞过敏者还会因使用红汞引发接触性皮炎，如今大部分医院都已弃之不用。</span></p><p><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">紫药水，又叫龙胆紫溶液，其杀菌能力介于碘酒与红药水之间。英国药理学家在动物实验中发现，让小白鼠系统地全身吸收龙胆紫，诱发癌症率高达100%。为此，英国卫生部发出通报，限制紫药水的使用范围，规定紫药水只能用于局部未破损的皮肤，严禁涂抹于破损的皮肤伤口上，以及口腔、肛门、尿路等粘膜处，严禁内服。</span></p><p class=\"p1\"><br/></p><p style=\"text-align: center;\"><img src=\"http://exploiter.oss-cn-beijing.aliyuncs.com/images/1496833898748.jpg\" alt=\"红药水和紫药水.jpg\"/></p><p><br/></p><p class=\"p1\"><span label=\"小标题\" style=\"color: rgb(51, 51, 51); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-weight: bold; font-family: 黑体;\"><strong>红药水和碘伏，绝不能一起用！</strong></span><span class=\"s1\"></span></p><p class=\"p1\"><span class=\"s1\"><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">如一定要使用红药水，切记，一定不可与碘伏一起使用。</span></span></p><p class=\"p2\"><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">如果碘与红药水的主要成分汞溴红混合在一起，会析出<span style=\"font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体; color: rgb(192, 80, 77);\">剧毒的碘化汞</span>！不仅失去了杀菌作用，还会腐蚀伤口组织，引起溃烂。这种碘化汞被人体吸收后，会导致汞中毒。</span><br/><span class=\"s1\"></span></p><p class=\"p1\"><span class=\"s1\"><strong><span label=\"小标题\" style=\"color: rgb(51, 51, 51); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-weight: bold; font-family: 黑体;\">百多邦软膏是抗生素类药物，对孩子安全吗？</span></strong></span></p><p class=\"p1\"><span class=\"s1\"><strong><span label=\"小标题\" style=\"color: rgb(51, 51, 51); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-weight: bold; font-family: 黑体;\"></span></strong></span></p><p class=\"p1\"><span style=\"font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体; color: rgb(192, 80, 77);\">安全！</span></p><p class=\"p2\"><span label=\"正文\" style=\"color: rgb(68, 68, 68); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-family: 黑体;\">百多邦是一种特殊类型的外用抗生素，对于很多皮肤上容易出现的细菌杀菌效果明显，而且不容易耐药，能大大减少和控制皮肤伤口的感染。百多邦只有在外用时才有杀菌效果，外用经皮肤穿透和吸收很少，即使进入体内，也会很快失活从肾脏排出。</span><br/><span class=\"s2\"></span></p><p class=\"p1\"><span class=\"s1\"><strong><span label=\"小标题\" style=\"color: rgb(51, 51, 51); font-size: 16px; line-height: 1; padding: 0px 0px 20px; font-weight: bold; font-family: 黑体;\"><br/></span></strong></span><br/></p><p><br/></p>\n" +
                "</body>\n" +
                "</html>"
    }
}
